function TurnONSwitch(n){$.ajax({url:"/Switches/TurnONAsync/",data:{id:n.id},dataType:"text",success:function(n){var t=$("#"+n),i;t.removeClass("orange");t.addClass("green");i=t.children("#"+n);i.html("OFF");i.attr("onclick","TurnOFFSwitch(this)");console.log("ON")},error:function(){console.log("Error")}})}function TurnOFFSwitch(n){$.ajax({url:"/Switches/TurnOFFAsync/",data:{id:n.id},dataType:"text",success:function(n){var t=$("#"+n),i;t.removeClass("green");t.addClass("orange");i=t.children("#"+n);i.html("ON");i.attr("onclick","TurnONSwitch(this)");console.log("OFF")},error:function(){console.log("Error")}})}